import React, { useState } from 'react';
import Card from './Card';

function App() {
  // Estados para los valores de los inputs y el mensaje de error
  const [brand, setBrand] = useState('');
  const [model, setModel] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [submitted, setSubmitted] = useState(false);

  // Manejo de cambios en los inputs
  const handleBrandChange = (event) => {
    setBrand(event.target.value);
  };

  const handleModelChange = (event) => {
    setModel(event.target.value);
  };

  // Manejo del envío del formulario
  const handleSubmit = (event) => {
    event.preventDefault();

    // Validación del primer input (brand)
    if (brand.length < 3 || brand.startsWith(' ')) {
      setErrorMessage('Por favor chequea que la información sea correcta');
      setSubmitted(false);
      return;
    }

    // Validación del segundo input (model)
    if (model.length < 6) {
      setErrorMessage('Por favor chequea que la información sea correcta');
      setSubmitted(false);
      return;
    }

    // Si pasa todas las validaciones
    setErrorMessage('');
    setSubmitted(true);
  };

  return (
    <div className="App">
      <h1>Información del Auto</h1>
      <form onSubmit={handleSubmit}>
        <input 
          type="text" 
          placeholder="Marca del auto" 
          value={brand} 
          onChange={handleBrandChange} 
        />
        <input 
          type="text" 
          placeholder="Modelo del auto" 
          value={model} 
          onChange={handleModelChange} 
        />
        <button type="submit">Enviar</button>
      </form>

      {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}

      {submitted && <Card brand={brand} model={model} />}
    </div>
  );
}

export default App;
